package controlador;

import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.scene.control.Button;

import java.io.IOException;

import javafx.event.ActionEvent;

import javafx.scene.control.Label;
import javafx.stage.Stage;

public class BatallaController {
	@FXML
	private Button atacar;
	@FXML
	private Button cambiarPokemon;
	@FXML
	private Label muestraTurno;
	

	// Event Listener on Button[#atacar].onAction
	@FXML
	public void atacar(ActionEvent event) throws IOException {
		// TODO Autogenerated
		// abre la ventana Captura
				FXMLLoader loader = new FXMLLoader(getClass().getResource("/vista/ElegirMovimientos.fxml"));

				Parent root = loader.load();
				
				ElegirMovimientosController controlador=loader.getController();

				Scene scene = new Scene(root);

				Stage stage = new Stage();
//				stage.initModality(Modality.APPLICATION_MODAL); (hace que la ventana sea modal)

				stage.setScene(scene);
				
				//espera hasta que se cierre ElegirMovimientos para seguir ejecutando c√≥difo
				stage.showAndWait();
				
				String texto=controlador.turno;
				
				muestraTurno.setText(texto);
				
				System.out.println("ha esperado");
		
	}
	// Event Listener on Button[#cambiarPokemon].onAction
	@FXML
	public void cambiarPokemon(ActionEvent event) {
		// TODO Autogenerated
	}
}
